all_unit_files = Dir.glob(GalaxyGame::Paths::UNITS_PATH.join("**", "*.json")); puts "First file: #{all_unit_files.first}"; unit_data = JSON.parse(File.read(all_unit_files.first)); puts "Unit ID from file: #{unit_data["id"]}"; service = Lookup::UnitLookupService.new; result = service.find_unit(unit_data["id"]); puts "Find by exact ID: #{result ? "found" : "nil"}"; result_upper = service.find_unit(unit_data["id"].upcase); puts "Find by uppercase ID: #{result_upper ? "found" : "nil"}"
