require 'json'; require 'set'; require_relative 'config/initializers/game_data_paths'; materials = Set.new; Dir.glob(GalaxyGame::Paths::BLUEPRINTS_PATH.join('units', '**', '*.json')).each do |file| begin; data = JSON.parse(File.read(file)); if data['required_materials']; data['required_materials'].keys.each { |mat| materials.add(mat) }; end; rescue; end; end; puts materials.to_a.sort.join('\n')
